    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        String id = idField.getText();
        int quantite = Integer.parseInt(quantiteField.getText());
        if(id.trim().equals("") || quantite < 1){
            JOptionPane.showMessageDialog(null, "L'ID ne doit pas être vide et la quantité doit être un nombre positif");
            return;
        }
        gach.insertAchat( id, quantite, jTable1, achatTable);
        idField.setText("");
        quantiteField.setText("");
        int total =0;
        for(int i =0;i<jTable1.getRowCount(); i++){
            int quan = Integer.parseInt(jTable1.getModel().getValueAt(i, 2).toString());
            int prix = Integer.parseInt(jTable1.getModel().getValueAt(i, 3).toString());
                total += prix*quan;
        }
        prixTot.setText("Le prix total du produits est: " + total);

    }//GEN-LAST:event_jButton1ActionPerformed

